import {
  GET_MEALS_FAIL,
  GET_MEALS_PENDING,
  GET_MEALS_SUCCESS,
} from "./actions";
import { mealListInitialState } from "./initialState";

const mealReducer = (state = mealListInitialState, action) => {
  switch (action.type) {
    case GET_MEALS_SUCCESS:
      return {
        ...state,
        mealList: action.payload,
        isLoading: false,
        error: null,
      };
    case GET_MEALS_PENDING:
      return {
        ...state,
        isLoading: true,
        error: null,
      };
    case GET_MEALS_FAIL:
      return {
        ...state,
        error: action.payload,
        isLoading: false,
      };
    default:
      return state;
  }
};

export default mealReducer;
