import React, { useRef } from "react";
import { BASE_URL } from "../../common/constant";
import Input from "../UI/Input";
import CartClasses from "./Cart.module.css";

const OrderForm = (props) => {
  const nameRef = useRef();
  const addressRef = useRef();
  const phoneRef = useRef();

  const submitCart = (event) => {
    event.preventDefault();

    props.submitCart({
      name: nameRef.current.value,
      address: addressRef.current.value,
      phone: phoneRef.current.value,
    });
  };

  return (
    <form onSubmit={submitCart}>
      <Input
        ref={nameRef}
        lable="Name: "
        input={{
          type: "string",
          style: {
            width: "100%",
          },
        }}
      />
      <Input
        ref={addressRef}
        lable="Address: "
        input={{
          type: "string",
          style: {
            width: "100%",
          },
        }}
      />
      <Input
        ref={phoneRef}
        lable="Phone: "
        input={{
          type: "number",
          style: {
            width: "100%",
          },
        }}
      />
      <div className={CartClasses.actions}>
        <button
          type="button"
          onClick={props.onCloseCart}
          className={CartClasses["button-alt"]}
        >
          Close
        </button>
        {<button className={CartClasses.button}>Confirm</button>}
      </div>
    </form>
  );
};

export default OrderForm;
