import { useContext, useState, useRef } from "react";
import CartContext from "../../../store/cartContext";
import Input from "../../UI/Input";
import classes from "./MealItemForm.module.css";

const MealItemForm = (props) => {
  const cartContext = useContext(CartContext);
  const [isValid, setIsvalid] = useState(false);
  const amountRef = useRef();
  const amount = amountRef.current.value; //even type of data is number, its value is always a string
  const enteredAmount = +amount; // transform string to number

  const onSubmitHandler = (event) => {
    event.preventDefault();
    cartContext.addItem();
    if (
      amount.trim().length === 0 ||
      amount.trim().length < 1 ||
      amount.trim().length > 5
    ) {
      setIsvalid(false);
    }
  };
  return (
    <form className={classes.form} onSubmit={onSubmitHandler}>
      <Input
        ref={amountRef}
        lable="Amount"
        input={{
          id: "amount+" + props.id,
          type: "number",
          min: "1",
          max: "5",
          step: "1",
          defaultValue: "1",
        }}
      />
      <button>+ Add</button>
      {!isValid && <p>Please provide a valid amount, enter 1 and 5</p>}
    </form>
  );
};

export default MealItemForm;
