import { ADD_CART_ITEM } from "./actions";

const cartReducer = (state, action) => {
  switch (action.type) {
    case ADD_CART_ITEM:
      let updatedItems = state.items.concat(action.payload);

      let existingItem = state.items.find((item) => item.id === action.payload);
      if (existingItem) {
        existingItem = { ...existingItem, amount: existingItem.amount + 1 };
        updatedItems = [...updatedItems, existingItem];
      }

      const updatedTotalAmount =
        state.totalAmount + action.payload.price * action.payload.amount;
      return {
        items: updatedItems,
        totalAmount: updatedTotalAmount,
      };
    default:
      return state;
  }
};

export default cartReducer;
